package com.kaushikVishal479.myexpenses.Entities;import androidx.room.ColumnInfo;import androidx.room.Entity;import androidx.room.Ignore;import androidx.room.PrimaryKey;import java.util.Date;@Entity(tableName = "expense")public class Expense {    @PrimaryKey(autoGenerate = true)    private int id;    @ColumnInfo(name = "price")    private double price;    @ColumnInfo(name = "date")    private Date date;    @ColumnInfo(name = "itemName")    private String itemName;    @ColumnInfo(name = "extra_detail")    private String extra;    public Expense(int id, double price, Date date, String itemName, String extra) {        this.id = id;        this.price = price;        this.date = date;        this.itemName = itemName;        this.extra = extra;    }    @Ignore    public Expense(double price, Date date, String itemName, String extra) {        this.price = price;        this.date = date;        this.itemName = itemName;        this.extra=extra;    }    public int getId() {        return id;    }    public void setId(int id) {        this.id = id;    }    public double getPrice() {        return price;    }    public void setPrice(double price) {        this.price = price;    }    public Date getDate() {        return date;    }    public void setDate(Date date) {        this.date = date;    }    public String getItemName() {        return itemName;    }    public void setItemName(String itemName) {        this.itemName = itemName;    }    public String getExtra() {        return extra;    }    public void setExtra(String extra) {        this.extra = extra;    }}